                                    actaExistente.FechaHito = _ppi.FechaHito;
                                    actaExistente.FechaAprobacion = _ppi.FechaAprobacion;
                                    actaExistente.FechaRechazo = _ppi.FechaRechazo;
                                    // actaExistente.Comentario = comentarioSeleccionado;
